import React from 'react';
import  { Component, useState, useEffect,useRef } from 'react';
import { Container, Row, Col } from "shards-react";
import StackGrid from "react-stack-grid";
import {getAllPlayers} from './query';
import Compare from './Compare';
import {
    Card,
    CardHeader,
    CardTitle,
    CardImg,
    CardBody,
    CardFooter,
    Button,
    Modal, ModalBody, ModalHeader,FormInput
  } from "shards-react";





const Player = (props) =>{

  let {client} = props;
  const [allPlayers,setAllPlayers] = useState();
  const [open,setModelOpen] = useState(false);
  const [newPlayer,setNewPlayer] = useState({
    playerName:"",
    runs:"",
    wickets:"",
    catches:"",
    matches:"",
    century:"",
    image:"",
  })


  useEffect(() => {
    client.query({
      query:getAllPlayers,
      variables:{}
    }).then((response)=>{

       let data = response.data.getAllPlayers;
       setAllPlayers(data);
    })
  }, [])

  const allStyles = {
      playersDiv:{
        width:"100%",
        height:"100%",
        border:"1px solid gray",
      }
  }

  const closePopup = () =>{  
  setModelOpen(!open);
  }

  const handleChange = (fieldName,value) =>{
     setNewPlayer({fieldName:value});
  }


  console.log("new",newPlayer);

    return(

        <div>
          <Container className="dr-example-container">
            <Row>
              <Col>
              <br/>
            <center><h4>Player Comparision</h4></center>
            <br/>

            <Compare client={client} />
            <br/>
            <center><h4>All Players</h4></center>
            <Button style={{float:"right"}} onClick={closePopup}>Add new Player +</Button>
            <br/>

            <StackGrid
              columnWidth={300}
             >

             {typeof allPlayers!="undefined" && allPlayers.length!=0 ? allPlayers.map((item,i)=>{
               return(
              <div key={i}>
                <Card style={{ maxWidth: "300px" }}>

             <CardImg style={{width:300,height:200}} src={item.image} />
             <CardBody>
            <CardTitle>{item.playerName}</CardTitle>
               <p>Runs: {item.runs} ; Wickets: {item.wickets} ; Catches: {item.catches} ; Matches: {item.Matches} ; Century: {item.century}</p>
            <Button>Compare</Button>
            </CardBody>

            </Card>
    </div>)
             }):null}   
            </StackGrid>
            </Col>
            </Row>
            </Container>



          <Modal open={open} toggle={closePopup}>
          <ModalHeader>Add New Player</ModalHeader>
          <ModalBody>

            <Row>
              <Col>
              <FormInput value={newPlayer.playerName} onChange={(e)=>handleChange("playerName",e.target.value)} placeholder="Player Name"/>
              </Col>
            </Row>
            <br/>
            <Row>
              <Col>
              <FormInput onChange={(e)=>handleChange("runs",e.target.value)} type="number" placeholder="runs"/>
              </Col>
              <Col>
              <FormInput onChange={(e)=>handleChange("wickets",e.target.value)}  type="number" placeholder="wickets"/>
              </Col>
            </Row>
            <br/>
            <Row>
              <Col>
              <FormInput onChange={(e)=>handleChange("catches",e.target.value)}  type="number" placeholder="catches"/>
              </Col>
              <Col>
              <FormInput onChange={(e)=>handleChange("matches",e.target.value)}  type="number" placeholder="matches"/>
              </Col>
            </Row>
            <br/>
            <Row>
              <Col>
              <FormInput type="number" onChange={(e)=>handleChange("century",e.target.value)}  placeholder="century"/>
              </Col>
              <Col>
              <FormInput type="text" onChange={(e)=>handleChange("image",e.target.value)}  placeholder="Player Image url"/>
              </Col>
            </Row>
            <br/>
            <center><Button>Save Player</Button></center>
          </ModalBody>
        </Modal>
        </div>
    )
}




export default Player;
