import gql from 'graphql-tag'

export const getAllPlayers = gql`
query getAllPlayers{
    getAllPlayers{
    playerName
    runs
    wickets
    catches
    matches
    century
    image
    _id
  }
}
`;

export const searchPlayer = gql`
query searchPlayer($playerName:String!){
    searchPlayer(playerName:$playerName){
    playerName
    runs
    wickets
    catches
    matches
    century
    image
    _id
  }
}
`;
