import React from 'react';
import  { Component, useState, useEffect,useRef } from 'react';
import { Container, Row, Col } from "shards-react";
import {searchPlayer} from './query';
import { ListGroup, ListGroupItem } from "shards-react";

import { FormInput } from "shards-react";

const allStyles = {
    compareDiv:{
        width:"50%",
        height:"100%",
        border:"1px solid red"

    },
    inputFieldLeft:{
        width:"30%",
        position:"relative",
        left:200,

    },
    inputFieldRight:{
        width:"30%",
        position:"relative",
        left:400,
        float:"left",

    },

    suggestion:{
        width:"100%",
        height:"100%",
        border:"1px solid red",

    }
}

const Compare =(props) =>{

    let {client} = props;

    const [player1,setPlayer1] = useState("");
    const [player2,setPlayer2] = useState("");
    const [suggestPlayer1,setSuggestPlayer1] = useState();
    const [suggestPlayer2,setSuggestPlayer2] = useState();


    const handleChange = (value,player) =>{
        console.log("value",value,"player",player);
        if(player=="p1")
        setPlayer1(value)
        else
        setPlayer2(value);

        let currentPlayer = player1=="" ? player2 : player1; 

        client.query({
            query:searchPlayer,
            variables:{playerName:currentPlayer}
          }).then((response)=>{

             let data = response.data.searchPlayer;
             console.log("dats",data);
             if(player=="p1")
             setSuggestPlayer1(data);
             else
             setSuggestPlayer2(data);
          })

    }

    console.log("suggest",suggestPlayer1);

return (
    <div>

    <Row>

          <Col>
          <FormInput id="player1" value={player1}  onChange={(e)=>handleChange(e.target.value,"p1",)} placeholder="Player 1" />
          <div style={allStyles.suggestion}>
          <ListGroup>
           {typeof suggestPlayer1!="undefined" && suggestPlayer1.length!=0 ? suggestPlayer1.map((item)=>{
               return(
           <ListGroupItem onClick={(e)=>setPlayer1(item.playerName)}>{item.playerName}</ListGroupItem> 
               )
           }):null}
        </ListGroup>
          </div>
          </Col>
          <Col>
          <FormInput id="player2" value={player2} onChange={(e)=>handleChange(e.target.value,"p2")} placeholder="Player 2" />
          <div style={allStyles.suggestion}>
          <ListGroup>
           {typeof suggestPlayer2!="undefined" && suggestPlayer2.length!=0 ? suggestPlayer2.map((item)=>{
               return(
           <ListGroupItem onClick={(e)=>setPlayer2(item.playerName)}>{item.playerName}</ListGroupItem> 
               )
           }):null}
        </ListGroup>
          </div>
          </Col>
        </Row>





    </div>
)
}


export default Compare;
