import React from 'react';
import  { Component, useState, useEffect,useRef } from 'react';
import { Container, Row, Col } from "shards-react";
import {searchPlayer} from './query';

import { FormInput } from "shards-react";

const allStyles = {
    compareDiv:{
        width:"50%",
        height:"100%",
        border:"1px solid red"

    },
    inputFieldLeft:{
        width:"30%",
        position:"relative",
        left:200,

    },
    inputFieldRight:{
        width:"30%",
        position:"relative",
        left:400,
        float:"left",

    },

    leftSuggestion:{
        width:"30%",
        height:"100%",
        border:"1px solid red",

    }
}

const Compare =(props) =>{

    let {client} = props;

    const [player1,setPlayer1] = useState("");
    const [player2,setPlayer2] = useState("");

    const handleChange = (value,player) =>{
        console.log("value",value,"player",player);
        if(player=="p1")
        setPlayer1(value)
        else
        setPlayer2(value);

        let currentPlayer = player1=="" ? player2 : player1; 

        client.query({
            query:searchPlayer,
            variables:{playerName:currentPlayer}
          }).then((response)=>{

             let data = response.data.searchPlayer;
             console.log("data",data);
          })

    }


return (
    <div>

    <Row>

          <Col>
          <FormInput id="player1" value={player1}  onChange={(e)=>handleChange(e.target.value,"p1",)} placeholder="Player 1" />
          <div style={allStyles.leftSuggestion}>dfd</div>
          </Col>
          <Col>
          <FormInput id="player2" value={player2} onChange={(e)=>handleChange(e.target.value,"p2")} placeholder="Player 2" />
          </Col>
        </Row>





    </div>
)
}


export default Compare;
