import React from 'react';
import  { Component, useState, useEffect,useRef } from 'react';
import { Container, Row, Col } from "shards-react";

import { FormInput } from "shards-react";

const allStyles = {
    compareDiv:{
        width:"50%",
        height:"100%",
        border:"1px solid red"

    },
    inputFieldLeft:{
        width:"30%",
        position:"relative",
        left:200,

    },
    inputFieldRight:{
        width:"30%",
        position:"relative",
        left:400,
        float:"left",

    }
}

const Compare =(props) =>{


    const [player1,setPlayer1] = useState("");
    const [player2,setPlayer2] = useState("");

    const handleChange = (han,val) =>{
       console.log("hannn,",han,"valueeeee",val);
       han(val)
    }


return (
    <div>

    <Row>

          <Col>
          <FormInput id="player1" value={player1}  onChange={(e)=>handleChange(setPlayer2,e.target.value)} placeholder="Player 1" />

          </Col>
          <Col>
          <FormInput id="player2" value={player2} onChange={(e)=>setPlayer2(setPlayer1,e.target.value)} placeholder="Player 2" />
          </Col>
        </Row>





    </div>
)
}


export default Compare;
